import os
import re
import pickle
import unittest
from NCBI_SSU import pull_16S_seq_NCBI as psN

PATH = os.path.dirname(os.path.abspath(__file__))
PPATH = re.sub('NCBI_SSU/tests','', PATH)
orgs_gbs = {'eco': ['GCF_000005845.2'], 'sbc': ['GCA_000020185.1']}

class RunNCBI_SSU(unittest.TestCase):
    def setUp(self):
        """Initialize before every test."""
        print ("Initializing tests")

        self.NS = psN.NCBI_SSU(orgs_gbs, 'testoutput.fa', wipe_folder=False, verbose=False, run=True)

    def tearDown(self):
        """Clean up after each test."""
        del self.NS

    def test_NCBI_SSU(self):
        """test NCBI_SSU class"""
        self.assertEqual(len(self.NS.default_SSU), 4842)
        
        seq16 = []
        GCF_000005845 = ""
        GCA_000020185 = ""

        with open(PPATH+'NCBI_SSU/data/testoutput.fa') as fin:
            for line in fin:
                line = line.strip()
                if line.startswith('>'):
                    pass
                else:
                    seq16.append(line)

        self.assertIn(GCA_000020185, seq16)
        self.assertIn(GCF_000005845, seq16)

if __name__ == '__main__':
    unittest.main(exit=False)
    os.remove(PPATH+'NCBI_SSU/data/testoutput.fa')